// ============================================
// Valida e parseia o webhook do GitHub (push event)
// ============================================

const body = $input.first().json.body;
const headers = $input.first().json.headers;

// Valida secret do webhook (opcional mas recomendado)
const githubEvent = headers['x-github-event'];

if (githubEvent !== 'push') {
  return [{ json: { ignore: true, reason: 'not_push_event', event: githubEvent } }];
}

// Filtra apenas commits que alteraram arquivos na pasta workflows/
const commits = body.commits || [];
const ref = body.ref || '';

// Só processa push na branch main
if (ref !== 'refs/heads/main') {
  return [{ json: { ignore: true, reason: 'not_main_branch', ref } }];
}

// Coleta todos os arquivos de workflow modificados/adicionados
const workflowFiles = new Set();

for (const commit of commits) {
  const allFiles = [
    ...(commit.added || []),
    ...(commit.modified || [])
  ];

  for (const file of allFiles) {
    if (file.startsWith('workflows/') && file.endsWith('.json')) {
      // Ignora o próprio deployer pra evitar loop
      if (!file.includes('gitops-deployer')) {
        workflowFiles.add(file);
      }
    }
  }
}

if (workflowFiles.size === 0) {
  return [{ json: { ignore: true, reason: 'no_workflow_changes' } }];
}

// Monta info do repo pra baixar os arquivos
const repoFullName = body.repository?.full_name || '';

return Array.from(workflowFiles).map(filePath => ({
  json: {
    ignore: false,
    filePath,
    fileName: filePath.split('/').pop().replace('.json', ''),
    repoFullName,
    branch: 'main',
    rawUrl: `https://raw.githubusercontent.com/${repoFullName}/main/${filePath}`,
    pusher: body.pusher?.name || 'unknown',
    commitMessage: commits[commits.length - 1]?.message || ''
  }
}));

// ============================================
// Busca se já existe um workflow com esse nome no n8n
// para decidir se faz CREATE ou UPDATE
// ============================================

const workflowData = $input.first().json;
const workflowName = workflowData.name;

// Guarda o JSON completo do workflow baixado
return [{
  json: {
    workflowName,
    workflowData,
    filePath: $('Tem Workflow Alterado?').first().json.filePath,
    pusher: $('Tem Workflow Alterado?').first().json.pusher,
    commitMessage: $('Tem Workflow Alterado?').first().json.commitMessage
  }
}];

// ============================================
// Decide se é CREATE ou UPDATE
// Busca workflow existente pelo nome
// n8n v2: campos read-only devem ser removidos
// ============================================

const prepData = $('Prepara Dados do Workflow').first().json;
const workflowName = prepData.workflowName;
const workflowData = prepData.workflowData;

const apiResponse = $input.first().json;
const existingWorkflows = apiResponse.data || apiResponse || [];

// Procura workflow com o mesmo nome
let existingId = null;
const workflows = Array.isArray(existingWorkflows) ? existingWorkflows : [];

for (const wf of workflows) {
  if (wf.name === workflowName) {
    existingId = wf.id;
    break;
  }
}

// Prepara o payload para a API do n8n
// Remove TODOS os campos read-only que a API rejeita
const cleanWorkflow = { ...workflowData };
delete cleanWorkflow.id;
delete cleanWorkflow.createdAt;
delete cleanWorkflow.updatedAt;
delete cleanWorkflow.active;
delete cleanWorkflow.versionId;
delete cleanWorkflow.tags;
delete cleanWorkflow.staticData;
delete cleanWorkflow.meta;
delete cleanWorkflow.pinData;

return [{
  json: {
    action: existingId ? 'update' : 'create',
    existingId,
    workflowName,
    workflowPayload: cleanWorkflow,
    filePath: prepData.filePath,
    pusher: prepData.pusher,
    commitMessage: prepData.commitMessage
  }
}];

// Extrai o ID do workflow criado/atualizado
const response = $input.first().json;
const context = $('Create ou Update?').first().json;

const body = response.body || response;
const statusCode = response.statusCode || 200;
const workflowId = body.id || context.existingId || 'unknown';

return [{
  json: {
    success: statusCode >= 200 && statusCode < 300,
    action: context.action,
    workflowName: context.workflowName,
    workflowId,
    filePath: context.filePath,
    pusher: context.pusher,
    commitMessage: context.commitMessage,
    statusCode,
    timestamp: new Date().toISOString()
  }
}];
