parameters.jsCode:
// Log conversas expiradas e registra eventos
const items = $input.all();

if (!items || items.length === 0 || !items[0].json.id) {
  return [{ json: { expired_count: 0 } }];
}

const results = items.map(item => ({
  json: {
    conversationId: item.json.id,
    remoteJid: item.json.remote_jid,
    pushName: item.json.push_name,
    messageCount: item.json.message_count,
    lastMessageAt: item.json.last_message_at
  }
}));

return results;

parameters.query:
INSERT INTO gk_events (conversation_id, event_type, event_data)
VALUES (
  '{{ $json.conversationId }}',
  'expired',
  '{"message_count": {{ $json.messageCount }}, "last_message_at": "{{ $json.lastMessageAt }}"}'::jsonb
);

parameters.jsCode:
const items = $input.all();

if (!items || items.length === 0 || !items[0].json.id) {
  return [{ json: { stalled_count: 0 } }];
}

return items.map(item => ({
  json: {
    conversationId: item.json.id,
    eventType: 'stalled_detected',
    eventData: JSON.stringify({ remote_jid: item.json.remote_jid })
  }
}));

parameters.query:
INSERT INTO gk_events (conversation_id, event_type, event_data)
VALUES (
  '{{ $json.conversationId }}',
  '{{ $json.eventType }}',
  '{{ $json.eventData }}'::jsonb
);
